import{_ as s,C as n,c as p,o as u,ap as l,j as a,a as t,G as i,w as o}from"./chunks/framework.B7RY4Fck.js";const P=JSON.parse('{"title":"خواب عمیق اپلیکیشن‌ها","description":"نحوه قرار دادن اپلیکیشن‌های سامسونگ در خواب عمیق برای جلوگیری از تخلیه سریع باتری","frontmatter":{"layout":"doc","outline":"deep","lang":"fa-IR","dir":"rtl","title":"خواب عمیق اپلیکیشن‌ها","description":"نحوه قرار دادن اپلیکیشن‌های سامسونگ در خواب عمیق برای جلوگیری از تخلیه سریع باتری","date":"2025-08-10T00:00:00.000Z","editelink":true},"headers":[],"relativePath":"topics/sleeping-apps.md","filePath":"topics/sleeping-apps.md"}'),d={name:"topics/sleeping-apps.md"};function g(b,e,c,m,h,_){const r=n("NolebaseInlineLinkPreview");return u(),p("div",null,[e[5]||(e[5]=l('<h1 id="مدیریت-مصرف-باتری-در-دستگاه‌های-اندرویدی" tabindex="-1">مدیریت مصرف باتری در دستگاه‌های اندرویدی <a class="header-anchor" href="#مدیریت-مصرف-باتری-در-دستگاه‌های-اندرویدی" aria-label="Permalink to &quot;مدیریت مصرف باتری در دستگاه‌های اندرویدی&quot;">​</a></h1><h2 id="تمرکز-بر-deep-sleep-apps-و-battery-protection" tabindex="-1">تمرکز بر Deep Sleep Apps و Battery Protection <a class="header-anchor" href="#تمرکز-بر-deep-sleep-apps-و-battery-protection" aria-label="Permalink to &quot;تمرکز بر Deep Sleep Apps و Battery Protection&quot;">​</a></h2><h2 id="مقدمه-مشکل-تخلیه-باتری-در-شب" tabindex="-1">مقدمه: مشکل تخلیه باتری در شب <a class="header-anchor" href="#مقدمه-مشکل-تخلیه-باتری-در-شب" aria-label="Permalink to &quot;مقدمه: مشکل تخلیه باتری در شب&quot;">​</a></h2><p>آیا تا به حال شده شب گوشی‌تان را کامل شارژ کنید (به ۱۰۰ درصد برسانید)، از شارژر جدا کنید، بخوابید و صبح ببینید بدون هیچ استفاده‌ای، شارژ به ۸۰، ۹۰ یا حتی کمتر از ۷۰ درصد رسیده؟ این مسئله رایج است و دلیل اصلی آن فعالیت‌های پس‌زمینه اپ‌ها پس از نیمه‌شب است. بسیاری از اپ‌ها طوری برنامه‌ریزی شده‌اند که دو شرط را چک کنند: ساعت بعد از ۱۲ شب باشد و شارژ گوشی بالای ۵۰ درصد. اگر این شرایط برقرار باشد، اپ‌ها فعال می‌شوند، همگام‌سازی انجام می‌دهند، گزارش‌ها را به توسعه‌دهنده ارسال می‌کنند و به‌روزرسانی‌ها را دانلود می‌کنند. این فعالیت‌ها باتری را به شدت مصرف می‌کنند.</p><br><p>برای جلوگیری از این مشکل، اندروید (به ویژه در رابط کاربری One UI سامسونگ) ابزارهایی مانند <strong>Background Usage Limits</strong> و <strong>Battery Protection</strong> ارائه می‌دهد. در ادامه، این ویژگی‌ها را گام‌به‌گام توضیح می‌دهم، با تمرکز بر قرار دادن اپ‌ها در حالت Deep Sleep. همچنین، نکات اضافی بر اساس دانش فنی و منابع معتبر اضافه کرده‌ام تا محتوا کامل‌تر شود.</p><br><h2 id="بخش-اول-background-usage-limits-محدودیت‌های-استفاده-پس‌زمینه" tabindex="-1">بخش اول: Background Usage Limits (محدودیت‌های استفاده پس‌زمینه) <a class="header-anchor" href="#بخش-اول-background-usage-limits-محدودیت‌های-استفاده-پس‌زمینه" aria-label="Permalink to &quot;بخش اول: Background Usage Limits (محدودیت‌های استفاده پس‌زمینه)&quot;">​</a></h2><p>این بخش در تنظیمات دستگاه &gt; Battery قرار دارد. هدف آن مدیریت اپ‌هایی است که در پس‌زمینه فعال می‌مانند و باتری مصرف می‌کنند. اسکرین‌شات مربوطه نشان‌دهنده گزینه‌های زیر است:</p>',9)),a("ul",null,[a("li",null,[e[2]||(e[2]=a("strong",null,"Put unused apps to sleep",-1)),e[3]||(e[3]=t(" (قرار دادن اپ‌های بلااستفاده در حالت خواب): این گزینه پیش‌فرض روشن است و اپ‌های unused را به طور خودکار به خواب می‌برد. اما بسیاری از اپ‌ها طوری طراحی شده‌اند که از این محدودیت فرار کنند و همچنان فعال بمانند. ")),i(r,{href:"https://www.reddit.com/r/GalaxyS22/comments/12ba345/app_battery_options_samsung_background_usage/",target:"_blank",rel:"noreferrer"},{default:o(()=>e[0]||(e[0]=[t("reddit")])),_:1,__:[0]}),e[4]||(e[4]=t()),i(r,{href:"https://www.samsung.com/ca/support/mobile-devices/galaxy-phone-sleeping-apps/",target:"_blank",rel:"noreferrer"},{default:o(()=>e[1]||(e[1]=[t("samsung")])),_:1,__:[1]})])]),e[6]||(e[6]=l('<br><ul><li><strong>Never auto sleeping apps</strong> (اپ‌هایی که هیچوقت به طور خودکار خواب نمی‌روند): اینجا می‌توانید اپ‌های ضروری مانند تلگرام یا واتس‌اپ را اضافه کنید تا همیشه فعال بمانند، نوتیفیکیشن‌ها را دریافت کنند و به‌روزرسانی شوند. مثلاً اگر نمی‌خواهید تلگرام خواب برود، آن را به این لیست اضافه کنید.</li></ul><br><ul><li><strong>Sleeping apps</strong> (اپ‌های خواب): اپ‌هایی که به این لیست اضافه شوند، به حالت خواب می‌روند اما ممکن است برای دریافت به‌روزرسانی یا نوتیفیکیشن بیدار شوند (گاهی با تأخیر). این حالت برای اپ‌های نیمه‌فعال مناسب است.</li></ul><br><ul><li><strong>Deep sleeping apps</strong> (اپ‌های خواب عمیق): این گزینه کلیدی است! اپ‌هایی که به این لیست اضافه شوند، کاملاً در پس‌زمینه غیرفعال می‌شوند. آن‌ها هیچ نوتیفیکیشنی نمی‌فرستند، به‌روزرسانی نمی‌گیرند و باتری مصرف نمی‌کنند تا زمانی که خودتان آن‌ها را باز کنید. مثلاً اپ‌هایی مانند Canva که سال‌هاست نصب کرده‌اید اما استفاده نمی‌کنید، ایده‌آل برای این لیست هستند. اگر این اپ‌ها در Deep Sleep نباشند، نوتیفیکیشن‌های مزاحم می‌فرستند، داده مصرف می‌کنند و باتری را هدر می‌دهند.</li></ul><br><h3 id="نکته-اضافی-مکانیسم-deep-sleep" tabindex="-1">نکته اضافی: مکانیسم Deep Sleep <a class="header-anchor" href="#نکته-اضافی-مکانیسم-deep-sleep" aria-label="Permalink to &quot;نکته اضافی: مکانیسم Deep Sleep&quot;">​</a></h3><ul><li>داده‌ها یا کش اپ‌ها پاک نمی‌شود؛ فقط اپ force-stop می‌شود تا وقتی باز شود.</li><li>تفاوت با Force Stop معمولی: پس از باز کردن و بستن از Recent Apps، دوباره به Deep Sleep برمی‌گردد.</li><li>پیشنهاد: هر ماه اپ‌هایی که اخیراً استفاده نشده‌اند را چک کنید و به Deep Sleep اضافه کنید. این کار می‌تواند مصرف باتری را تا ۲۰-۳۰ درصد کاهش دهد (بر اساس تجربیات کاربران).</li></ul><br><ul><li>در اندروید ۱۱ و بالاتر، سامسونگ به طور پیش‌فرض اپ‌ها را در پس‌زمینه محدود می‌کند، اما Deep Sleep کنترل دقیق‌تری می‌دهد.</li></ul><br><h2 id="بخش-دوم-battery-protection-حفاظت-باتری" tabindex="-1">بخش دوم: Battery Protection (حفاظت باتری) <a class="header-anchor" href="#بخش-دوم-battery-protection-حفاظت-باتری" aria-label="Permalink to &quot;بخش دوم: Battery Protection (حفاظت باتری)&quot;">​</a></h2><p>این ویژگی هم در تنظیمات &gt; Battery قرار دارد و برای جلوگیری از آسیب باتری هنگام شارژ طولانی‌مدت طراحی شده. اسکرین‌شات نشان‌دهنده گزینه‌های زیر است:</p><ul><li><strong>Basic</strong>: وقتی باتری به ۱۰۰ درصد برسد، شارژ متوقف می‌شود تا به ۹۵ درصد برسد، سپس ادامه می‌دهد. این چرخه ساعت‌ها طول می‌کشد و باتری را ایمن نگه می‌دارد.</li></ul><br><ul><li><strong>Adaptive</strong>: در زمان خواب (بر اساس الگوی استفاده شما)، به حالت Maximum می‌رود و قبل از بیدار شدن به Basic سوئیچ می‌کند. ایده‌آل برای شارژ شبانه.</li></ul><br><ul><li><strong>Maximum</strong>: شارژ در ۸۰ درصد متوقف می‌شود. امن‌ترین گزینه برای عمر طولانی باتری، به ویژه اگر گوشی نو است.</li></ul><br><p>پیش‌فرض ممکن است خاموش باشد، پس حتماً روشن کنید. در اندروید ۱۳ و پایین‌تر، این ویژگی نبود و چرخه شارژ مداوم باتری را تخریب می‌کرد.</p><h3 id="نکته-اضافی-چرا-۸۰-درصد-ایمن‌تر-است؟-توضیح-فنی-باتری-لیتیوم-یون" tabindex="-1">نکته اضافی: چرا ۸۰ درصد ایمن‌تر است؟ (توضیح فنی باتری لیتیوم-یون) <a class="header-anchor" href="#نکته-اضافی-چرا-۸۰-درصد-ایمن‌تر-است؟-توضیح-فنی-باتری-لیتیوم-یون" aria-label="Permalink to &quot;نکته اضافی: چرا ۸۰ درصد ایمن‌تر است؟ (توضیح فنی باتری لیتیوم-یون)&quot;">​</a></h3><p>باتری‌های گوشی از الکترود آند (معمولاً گرافیت) و کاتود (اکسید لیتیوم مانند NMC) تشکیل شده‌اند، با الکترولیت بین آن‌ها. تا ۸۰ درصد، یون‌های لیتیوم ۸۰ جا می‌گیرند، اما بعد از آن:</p><ul><li>نیاز به ولتاژ بالاتر برای نفوذ یون‌ها، که مقاومت را افزایش می‌دهد و انرژی به گرما تبدیل می‌شود.</li><li>لایه SEI روی آند رشد می‌کند یا تجزیه می‌شود، گرما تولید می‌کند و ممکن است گاز ایجاد کند (پف کردن باتری).</li><li>پلاریزاسیون (جمع شدن بار روی الکترودها) مقاومت را بالا می‌برد و گرما بیشتر می‌شود.</li><li>فاز CV (Constant Voltage) پس از ۸۰ درصد کند است و گرمای بیشتری تولید می‌کند.</li></ul><p>طبق قانون آرنیوس، هر ۱۰ درجه افزایش دما، سرعت تخریب را ۲ برابر می‌کند. مثلاً اگر دما به ۵۲ درجه برسد (۲۰ درجه بیشتر از معمول)، تخریب ۴ برابر می‌شود! محدود کردن به ۸۰ درصد می‌تواند عمر باتری را دو برابر کند.</p><br><ul><li>نکته اضافی: فست شارژ را خاموش کنید اگر ممکن است، زیرا گرمای بیشتری تولید می‌کند (به ویژه در برندهایی مانند شیائومی). از Routines در One UI برای اتوماتیک کردن استفاده کنید، مثلاً Battery Protection را در ۹۰ درصد فعال کنید.</li></ul><br><h2 id="نتیجه‌گیری-و-پیشنهادها" tabindex="-1">نتیجه‌گیری و پیشنهادها <a class="header-anchor" href="#نتیجه‌گیری-و-پیشنهادها" aria-label="Permalink to &quot;نتیجه‌گیری و پیشنهادها&quot;">​</a></h2><p>با استفاده از Deep Sleep برای اپ‌های غیرضروری و فعال کردن Battery Protection (ترجیحاً Adaptive یا Maximum)، می‌توانید مصرف باتری را به حداقل برسانید و عمر دستگاه را افزایش دهید. این تغییرات ساده اما مؤثر هستند و هیچ داده‌ای را از دست نمی‌دهید.</p>',30))])}const S=s(d,[["render",g]]);export{P as __pageData,S as default};
